<?= Asset::js('auth/contact.js');?><div class="container content">	<?php if($errmsg != "") { ?>		<div class="alert alert-danger"><?= $errmsg ?></div>	<?php } ?>	<div class="panel panel-default">		<div class="panel-heading">			<i class="fa fa-check-square-o"></i>&nbsp;お問い合わせフォーム		</div>		<div class="panel-body">			<form action="<?= Uri::create("auth/contact_send") ?>" method="POST" class="form-horizontal contact-form" role="form">				<div class="form-group">					<label class="control-label" for="inputName">お名前：</label>					<div class="form-control-box">						<input type="text" name="name" class="form-control" id="inputName" placeholder="お名前" maxlength="30" autofocus value="<?= $name ?>" <?php if($login) { echo 'disabled'; }?> />					</div>				</div>				<div class="form-group">					<label class="control-label" for="inputEmail">メールアドレス：</label>					<div class="form-control-box">						<input type="text" name="email" class="form-control" id="inputEmail" placeholder="メールアドレス" maxlength="35" value="<?= $email ?>"  <?php if($login) { echo 'disabled'; }?>/>					</div>				</div>				<div class="form-group">					<label class="control-label" for="form_screen">画面名：</label>					<div class="form-control-box">						<?= Form::select('screen', $screen, Utility::get_constant_name('contact_screen'), array('class'=>'form-control')); ?>					</div>				</div>				<div class="form-group">					<label class="control-label" for="inputContent">お問い合わせ内容：</label>					<div class="form-control-box">						<textarea name="content" class="form-control" id="inputContent" placeholder="お問い合わせ内容を入力してください。" style="width: 400px; height: 150px;"><?= $content ?></textarea>					</div>					<br>				</div>				<div class="form-group">					<label class="control-label"></label>					<div class="form-control-box">						<button id="submit_button" type="submit" class="btn btn-primary" data-loading-text="送信中...">							<i class="fa fa-check"></i>&nbsp;送信						</button>					</div>				</div>			</form>		</div>	</div></div>